import { useState } from "react";
import Input from "../../components/Input";
import {
  Container,
  Button,
  ButtonContainer,
  CategoryBox,
  CategoryContainer,
  CategoryImage,
  Form,
  FormTitle,
  MapContainer,
  Section,
} from "./styles";
import { LatLngExpression, LeafletMouseEvent } from "leaflet";
import { Marker, TileLayer } from "react-leaflet";
import { categories } from "./categories";
import useGetLocation from "../../hooks/useGetLocation";

export default function New() {
  const [formValues, setFormValues] = useState({
    name: "",
    description: "",
    contact: "",
    category: "",
    coords: [0 , 0]
  });

  const { coords } = useGetLocation();

  if (!coords) {
    return <h1>Obtendo localizacao ...</h1>
  }

  return (
    <Container>
      <Form>
        <FormTitle>Cadastro do comercio local</FormTitle>
        <Section>Dados</Section>
        <Input
          label="nome do local"
          name="name"
          value={formValues.name}
          onChange={setFormValues}
        />

        <Input
          label="Descricao"
          name="description"
          value={formValues.description}
          onChange={setFormValues}
        />

        <Input
          label="Contato"
          name="contact"
          value={formValues.contact}
          onChange={setFormValues}
        />

        <Section>Endereco</Section>

        <MapContainer
          center={
            {
              lat: coords[0],
              lng: coords[1],
            } as LatLngExpression
          }
          zoom={13}
          whenCreated={(map) => {
              map.addEventListener('click' , (event: LeafletMouseEvent) => {
                setFormValues(prev => ({...prev, coords : [event.latlng.lat , event.latlng.lng]}))
              })
          }}
        >
          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={[formValues.coords[0], formValues.coords[1]] as LatLngExpression} />
        </MapContainer>
        <Section>Categoria</Section>
        <CategoryContainer>
          {categories.map((category) => {
            return (
              <CategoryBox
                key={category.key}
                onClick={() => {
                  setFormValues((prev) => ({
                    ...prev,
                    category: category.key,
                  }));
                }}
                isActive={formValues.category === category.key}
              >
                <CategoryImage src={category.url} />
                {category.label}
              </CategoryBox>
            );
          })}
        </CategoryContainer>

        <ButtonContainer>
          <Button type="submit">Salvar</Button>
        </ButtonContainer>
      </Form>
    </Container>
  );
}
