import { useState } from "react";
import Input from "../../components/Input";
import {
  Container,
  Button,
  ButtonContainer,
  CategoryBox,
  CategoryContainer,
  CategoryImage,
  Form,
  FormTitle,
  MapContainer,
  Section,
} from "./styles";
import { LatLngExpression } from "leaflet";
import { Marker, TileLayer } from "react-leaflet";
import { categories } from "./categories";

export default function New() {
  const [formValues, setFormValues] = useState({
    name: "",
    description: "",
    contact: "",
    category: "",
  });

  console.log(formValues);

  return (
    <Container>
      <Form>
        <FormTitle>Cadastro do comercio local</FormTitle>
        <Section>Dados</Section>
        <Input
          label="nome do local"
          name="name"
          value={formValues.name}
          onChange={setFormValues}
        />

        <Input
          label="Descricao"
          name="description"
          value={formValues.description}
          onChange={setFormValues}
        />

        <Input
          label="Contato"
          name="contact"
          value={formValues.contact}
          onChange={setFormValues}
        />

        <Section>Endereco</Section>

        <MapContainer
          center={
            {
              lat: 12,
              lng: 23,
            } as LatLngExpression
          }
          zoom={13}
          whenCreated={() => {}}
        >
          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={[12, 23] as LatLngExpression} />
        </MapContainer>
        <Section>Categoria</Section>
        <CategoryContainer>
        {categories.map((categoria)=>{
          return (
              <CategoryBox 
              key={categoria.key}
              onClick={()=>{
                setFormValues(prev => ({...prev, category : categoria.key}))
              }}
              isActive={formValues.category === categoria.key}
              />
            )
          })}
          </CategoryContainer>
      </Form>
    </Container>
  );
}
